CREATE TABLE core_schema.monasteries (
    id BIGINT AUTO_INCREMENT PRIMARY KEY,
    name_ro VARCHAR(255) NOT NULL,
    name_en VARCHAR(255),
    name_ru VARCHAR(255),
    description_ro TEXT,
    description_en TEXT,
    description_ru TEXT,
    latitude DOUBLE PRECISION,
    longitude DOUBLE PRECISION
);

